import React from 'react';
import './Calendar.css';

function Calendar() {

  const selectedYear= new Date().getFullYear();
  const selectedMonth= new Date().getMonth();

  // Return Total no. of days in a month
  const daysInMonth = function(month, year){
    return new Date(year, month+1, 0).getDate()
  }
  // Return day of the day
  const monthDays= function(date){
    return new Date(date).getDay();
  }

  return (
    <div>
      <div className='calendar-container'>
        <div className='calendar-inner-container'>
          <h2>Calender</h2>
          <div id='calendar-display'>
            <div className='month-header'>
              <div className='left-arrow'>{'<'}</div>
              <div id='month'>November</div>
              <div className='right-arrow'>{'>'}</div>
            </div>
            <div className='calendar-container-child'>
              <div>Sunday</div>
              <div>Monday</div>
              <div>Tuesday</div>
              <div>Wednesday</div>
              <div>Thrusday</div>
              <div>Friday</div>
              <div>Saturday</div>
            </div>
            <div className='calendar-container-child'>
               {[1,2,3,4,5].map((item, index)=> (  
                  <div>{item}</div> 
                ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Calendar
