import React from 'react';
import './Card.css';
import axios from 'axios';
import { url } from '../../resoucres/resource';
import AddBooks from '../AddBooks/AddBooks';

function Card({id, author,language, pages ,title,summary, getBooks}) {

  // delete book function

  const deleteBook = () =>{
    axios.delete(`${url}/${id}`).then(data=> console.log(data));
    getBooks(); 
  }


  const updateBook =() =>{

    // check if the button if clickd or not
    console.log("edit clicked");

    // received the data through get request 

    axios.get(`${url}/${id}`).then(data=>console.log(data));



 // create an object with  received data

    let formJSON = {
      title : title,
      author : author,
      language : language,
      pages : pages,
      summary : summary,
      id: id
    }

    console.log(formJSON)

    // populat the form

    // axios.put(`${url}/${id}`, formJSON)

  }


  return (
    <div className = 'cards'>
      <div className = 'cardTitle'>Title: {title}</div>
      <div className= 'cardDiscpription'>
        <p>Author: {author}</p>
        <p>Language: {language}</p>
        <p>Pages: {pages}</p>
      </div>
      <div className= 'cardSummary'>{summary}</div>

      <div className = 'cardButton'>
        <button  onClick= {updateBook} className = 'button'>Edit</button>
        <button   onClick= {deleteBook} className = 'button'>Delete</button>
      </div>

    </div>

  )
}

export default Card
