// To find the days in a month 


export const getDays = (startDay, enDay) => {

    const day = [];
  // put empty string for enpty days
    for(let i=0; i<=startDay; i++){
      day.push("");
    }
  //  To get total no. of days in a month
    for(let i=1; i<=enDay; i++){
      day.push(i);
    }
    return day
  }
