import { CHANGE_MONTH } from "../actions/calendarAction";
import { getDays } from "../utilts/calendarUtil";
import { selectedYear, selectedMonth, monthDays, daysInMonth } from "../utilts/dateUtil";




// new Date('date').getDay();
const startDay = monthDays(`${selectedMonth+1}-01-${selectedYear}`); 

const endDay = daysInMonth(selectedMonth, selectedYear);

const initialState= {
    startDay,
    endDay,
    selectedMonth,
    selectedYear, 
    days: getDays(startDay, endDay)
}

export const reducers = (state=initialState, action)=>{
    switch(action.type){ 
        case 'CHANGE_MONTH':
            // logic to change the startDay, endDay and days
            return{...state}

        default: 
            return state;
    }
}
